function check_quantity(n){for(var i=0,r=document.getElementById("quantity:"+n),t=0;t<r.length;t++)r[t].selected&&(i=eval(i)+eval(r[t].value));var u=document.getElementById("constPrice:"+n).innerHTML,f=u.replace(",","."),e=i*f;document.getElementById("price:"+n).innerHTML=e.toFixed(2)}function calculate_total(){for(var r,t=document.getElementsByTagName("calculatedPrice"),i=0,n=0;n<t.length;n++)r=t[n].innerHTML.replace(",","."),i+=parseFloat(r);document.getElementById("result").innerHTML=i.toFixed(2)}calculate_total();