function check_quantity(n){for(var i=0,r=document.getElementById("quantity:"+n),t=0;t<r.length;t++)r[t].selected&&(i=eval(i)+eval(r[t].value));var u=document.getElementById("constPrice:"+n).innerHTML,f=u.replace(",","."),e=i*f;document.getElementById("price:"+n).innerHTML=e.toFixed(2)}function calculate_total(){for(var t,i=document.getElementsByTagName("calculatedPrice"),r=0,n=0;n<i.length;n++)t=i[n].innerHTML.replace(",","."),r+=parseFloat(t);document.getElementById("result").innerHTML=r.toFixed(2)}calculate_total();